// import logo from './logo.svg';
import React, { useState, useEffect } from 'react'
import Post from './components/Post.js';
import './App.css';
import { db } from './firebase';
import Modal from '@material-ui/core/Modal';
import { makeStyles } from '@material-ui/core/styles';
import { Button, Input } from '@material-ui/core';

// MODAL IN
// MATERIAL-UI STYLING
function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
};

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  }
}));



function App() {
  // MATERIAL-UI HOOK
  // getModalStyle is not a pure function, we roll the style only on the first render
  const classes = useStyles();
  const [modalStyle] = useState(getModalStyle);

  // MODAL SIGNUP FORM
  const [username, setUsername] = useState([]);
  const [email, setEmail] = useState(false);
  const [password, setPassword] = useState(false);

  // STATE HOOK -new in React 16.8 {allows for use of state without class}
  const [posts, setPosts] = useState([]);
  const [open, setOpen] = useState(false);

  // useEffect(), runs code based on specific condition
  // Similar to componentDidMount and componentDidUpdate:
  useEffect(() => {
    // code runs once when app component loads
    db.collection('posts').onSnapshot(snapshot => {
      //onSnapShot() powerful listener
      setPosts(snapshot.docs.map(doc => doc.data()))
    })
  }, []);
  // code reruns whenever [object] changes





  return (
    <div className="App">
      <Modal
        open={open}
        onClose={() => setOpen(false)}
      >
      <div style={modalStyle} className={classes.paper}>
        <form className="app_signup">
      <Input
      type="text" 
      placeholder="Username"
      value={username}
      onChange={(e) => setUsername(e.target.value)}
      />

      <Input
      type="email" 
      placeholder="email"
      value={email}
      onChange={(e) => setEmail(e.target.value)}
      />

      <Input
      type="password" 
      placeholder="Password"
      value={password}
      onChange={(e) => setPassword(e.target.value)}
      />

      <Button onClick={(signUp) => setOpen(true)}>Create</Button>

        </form>
      </div>
      </Modal>

      <div className="app_header">
        <img className="app_header_image"
          src={"/../image/header.png"}
          alt="logo" />
      </div>



      <Button onClick={() => setOpen(true)}>Sign-Up</Button>


      <h5>Image based social media on ReactJS</h5>

      {
        posts.map(post => (
          <Post avatarUrl={post.avatarUrl} username={post.username} location={post.location} imageUrl={post.imageUrl} caption={post.caption} />
        ))
      }

    </div>
  );
}

export default App;
